const cubeCostsList = [ 10, 40, 100, 170, 260, 360, 490, 630, 790, 990, 1210, 1380, 1570, 1860, 2070, 2290, 2510, 2910, 3160, 3420, 3690, 4220, 4520, 4820, 5130, 5450, 6190, 6540, 6900, 7260, 7630, 8630, 9040, 9460, 9890, 10320, 11650, 12130, 12620, 13120, 13620, 15410, 15970, 16530, 17100, 17680, 20100, 20750, 21400, 22070, 22740, 26020, 26780, 27550, 28320, 29100, 29890, 34530, 35430, 36340, 37260, 38190, 39120, 40060, 46870, 47960, 49060, 50170, 51290, 52420, 53550, 54690, 65150, 66500, 67860, 69230, 70610, 72000, 73400, 74800, 76220, 84700, 86260, 87830, 89410, 91010, 92610, 94210, 95830, 97460, 109010, 110820, 112640, 114470, 116310, 118160, 120020, 121890, 123770, 126000, 129000, 132000, 135000, 138000, 141000, 144000, 147000, 150000, 154000, 156000, 159000, 162000, 165000, 168000, 172000, 176000, 180000, 184000, 188000, 192000, 196000, 200000, 204000, 208000, 212000, 216000, 220000, 224000, 228000, 232000, 247000, 262000, 278000, 295000, 313000, 333000, 353000, 375000, 399000, 424000, 451000, 480000  ]
const troopsData = []
let totalCores = 0;
let totalCubes = 0;

for (let i = 0; i < 2000; i++) {
  let lvl = i+1;
  let cubeCost = (lvl % 10 === 0) ? cubeCostsList[(lvl/10)-1] : 0;

  let coreCost = (lvl < 1000) ? 
    Math.round(8 + 2 * lvl**1.5) 
    : 
    (lvl < 1500) ? 
      Math.round(1.275 * ((lvl)**2) - 1211601)
      :
      Math.round(4.5 * ((lvl)**2) - 8467851)

  const powerBase = (lvl > 1000) ?
    Math.floor(1.5*((lvl**1.5)-10489))
    :
    Math.floor(98 + Math.sqrt(lvl) * (lvl*2))

  troopsData.push({
    "level": lvl,
    "powerBase": powerBase,
    "cubeCost": cubeCost,
    "coreCost": coreCost,
    "totalCoreCost": totalCores,
    "totalCubeCost": totalCubes
  })
  totalCores += coreCost
  totalCubes += cubeCost

}